const Course = (course) => {
    return (
        <div>
            {course.map(course => {
                return (
                    <>
                    <Header course={course} />
                    <Content parts={parts} />
                    <Total sum={parts[0].exercises + parts[1].exercises + parts[2].exercises} />
                    </>
                )
            })}
        </div>
    )
}

const Header = ({ name }) => <h1>{name}</h1>

const Content = ({ parts }) =>
        <Part
            part={parts[0]}
        />


const Part = ({ part }) =>
    <p>
        {part.name} {part.exercises}
    </p>

const Total = ({ sum }) => <p>Number of exercises {sum}</p>





export default Course
